    public void agregarQueryParaInsertarCFDI(String tipoDocumento,String tipoFactura) throws Exception{      
        char tipoDocumentoChar='0';
        char tipoComprobanteChar='0';
        if(tipoDocumento.equals("CFDI"))
            tipoDocumentoChar='0';
        if(tipoDocumento.equals("Nota de crédito"))
            tipoDocumentoChar='1';
        if(tipoDeComprobante.equals("ingreso"))
            tipoComprobanteChar='0';
        if(tipoDeComprobante.equals("egreso"))
            tipoComprobanteChar='1';
        if(tipoDeComprobante.equals("traslado"))
            tipoComprobanteChar='2';
        int tipoDetalle=0;    //Particulares
        if(tipoFactura.equals("PublicoGral")||tipoFactura.equals("PublicoGralConTDC"))
            tipoDetalle=1;       //venta al público
        else if(tipoFactura.equals("Paquetes"))
            tipoDetalle=2;       //paquetes
        else if (tipoFactura.equals("Cuentas"))
            tipoDetalle=3;       //servicios: cuentas
        else if(tipoFactura.equals("Rentas"))
            tipoDetalle=4;       //Rentas
        else if(tipoFactura.equals("Credito-Empresas"))
            tipoDetalle=5;       //Crédito empresas
        SimpleDateFormat sdf=new SimpleDateFormat("yyyy-MM-dd HH:mm:ss"); 
        sQuery ="select * from insertacfdi('"+this.serie+"'::character varying,'"+Integer.parseInt(this.folio)+"'::int2,'"
                    +this.version+"'::character,'"+this.emisor.getRfc()+"'::character varying,'"+this.receptor.getRfc()+"'::character varying,'"
                    +tipoComprobanteChar+"'::character varying,'"+tipoDocumentoChar+"'::character varying,'"+this.metodoDePago+"'::character varying,'"
                    +this.formaDePago+"'::character varying,'"+this.numCtaPago+"'::character varying,'"+this.moneda+"'::character varying,"
                    +this.subTotal+","+this.descuento+","+this.impuestos.totalImpuestosTrasladados+",'"+sdf.format(fecha)+"'::timestamp without time zone,'"+this.sello+"'::text,'"
                    +this.noCertificado+"'::text,'"+this.certificado+"'::text,'"+this.lugarExpedicion+"'::character varying,"+this.total+",'"+
                this.sUuid+"','"+tipoDetalle+"'::character);";
    }
