    public void actualizarDatosReceptor() throws Exception {
        Vector rst = null;
        Vector<Receptor> vObj = null;
        sQuery = "select * from buscallavereceptor('"+receptor.getRfc()+"')";
        if (getAD() == null){
            setAD(new AccesoDatos());
            getAD().conectar();
        }
        rst = getAD().ejecutarConsulta(sQuery);
        getAD().desconectar();
        setAD(null);
        if (rst != null && !rst.isEmpty()){
            receptor = new Receptor();
            Vector vRowTemp = (Vector)rst.elementAt(0); 
            receptor.setRfc((String) vRowTemp.elementAt(0));
            receptor.setNombre((String) vRowTemp.elementAt(1));
            receptor.setCorreoElectronico((String) vRowTemp.elementAt(2));      
            receptor.setDomicilio(receptor.buscarDomicilio(((Double) vRowTemp.elementAt(3)).intValue()));            
       }        
    }

