        /*
         * Modifica un emisor
         */
        public int modificarEmisor() throws Exception{
            int nRet = 0;
            String sQuery = "";
            if (this.rfc.equals("")||this.domicilioFiscal.pais.equals("") ) {
                throw new Exception("Emisor.modificar: error de programación, faltan datos");
            }else {
                if (getAD() == null){
                    setAD(new AccesoDatos());
                    getAD().conectar();
                }
                /*
                 * En caso de que el domicilio fiscal no sea el mismo que el de expedición, 
                 * modificará el registro correspondiente al domicilio de expedición
                 */
                if(this.dom_fiscal_dom_exp==0){
                    sQuery = "update tubicacion set calle='"+this.expedidoEn.calle+"',"
                            + "scolonia='"+this.expedidoEn.colonia+"',"
                            + "snoexterior='"+this.expedidoEn.noExterior+"',"
                            + "snointerior='"+this.expedidoEn.noInterior+"',"
                            + "sreferencia='"+this.expedidoEn.referencia+"',"
                            + "spais='"+this.expedidoEn.pais+"',"
                            + "scodpostal='"+this.expedidoEn.codigoPostal+"' "
                        + "where tubicacion.idtubicacion=(select expedido_en "
                        + "from emisor where emisor.rfc='"+this.rfc+"');";
                    nRet = getAD().ejecutarComando(sQuery);
                }
                /*
                 * Actualiza domicilio fiscal
                 */
                sQuery = "update t_ubicacion_fiscal set calle='"+this.domicilioFiscal.calle+"',"
                            + "colonia='"+this.domicilioFiscal.colonia+"',"
                            + "no_exterior='"+this.domicilioFiscal.noExterior+"',"
                            + "no_interior='"+this.domicilioFiscal.noInterior+"',"
                            + "referencia='"+this.domicilioFiscal.referencia+"',"
                            + "pais='"+this.domicilioFiscal.pais+"',"
                            + "cod_postal='"+this.domicilioFiscal.codigoPostal+"' "
                        + "where t_ubicacion_fiscal.id_t_ubicacion_fiscal=(select domicilio_fiscal "
                        + "from emisor where emisor.rfc='"+this.rfc+"');";
                
                nRet = getAD().ejecutarComando(sQuery);
                /*
                 * Actualiza el nombre del emisor
                 */
                sQuery = "update emisor set nombre='"+this.nombre+"' where emisor.rfc='"+this.rfc+"';" ;
                nRet = getAD().ejecutarComando(sQuery);
                getAD().desconectar();
                setAD(null);
            }
            return nRet; 
        }
        
