        /*
         * Busca un domicilio de receptor a partir de el id de domicilio (extraido de la bd)
         */
       
        public TUbicacion buscarDomicilio(int x) throws Exception{
            TUbicacion oP=null;
            Vector rst = null;
            Vector<TUbicacion> vObj = null;
            String sQuery = "";
            int i=0;
            sQuery = "SELECT * from buscallavetubicacion("+x+")";
            if (getAD() == null){
                setAD(new AccesoDatos());
                getAD().conectar();
            }
            rst = getAD().ejecutarConsulta(sQuery);
            getAD().desconectar();
            setAD(null);
            if (rst != null) {
                vObj = new Vector<TUbicacion>();   
                    oP = new TUbicacion();
                    Estado estado=new Estado();
                    Ciudad ciudad=new Ciudad();
                    Municipio municipio=new Municipio();
                    Pais pais=new Pais();
                    String edo,mun,loc="";
                    Vector vRowTemp = (Vector)rst.elementAt(i);
                    oP.setIdTUbicacion(((Double) vRowTemp.elementAt(0)).intValue());
                    if(((String) vRowTemp.elementAt(1)!=null)&&!(((String) vRowTemp.elementAt(1)).equals("")))
                        oP.setCalle((String) vRowTemp.elementAt(1));
                    if(((String) vRowTemp.elementAt(2)!=null)&&!(((String) vRowTemp.elementAt(2)).equals("")))
                        oP.setNoExterior((String) vRowTemp.elementAt(2));   
                    if(((String) vRowTemp.elementAt(3)!=null)&&!(((String) vRowTemp.elementAt(3)).equals("")))
                        oP.setNoInterior((String) vRowTemp.elementAt(3));
                    if(((String) vRowTemp.elementAt(4)!=null)&&!(((String) vRowTemp.elementAt(4)).equals("")))
                        oP.setColonia((String) vRowTemp.elementAt(4));
                    if(((String) vRowTemp.elementAt(5)!=null)&&!(((String) vRowTemp.elementAt(5)).equals("")))
                        oP.setReferencia((String) vRowTemp.elementAt(5));
                    edo=((String) vRowTemp.elementAt(6));
                    if(edo!=null&&!edo.equals("")){
                        oP.setEstado(oP.buscarEstado(edo));
                        estado.setDescrip(oP.getEstado());
                        estado.setCve(edo);
                        oP.setEdo(estado);
                        oP.setPais(oP.buscarPais(edo));
                        pais.setDescrip(oP.getPais());
                        oP.setObjPais(pais);
                        mun=((String) vRowTemp.elementAt(7));
                        if(mun!=null&&!mun.equals("")){
                            oP.setMunicipio(oP.buscarMunicipio(edo,mun));
                            municipio.setCve(mun);
                            municipio.setEdo(oP.getEdo());
                            municipio.setDescrip(oP.getMunicipio());
                            oP.setMun(municipio);
                            loc=((String) vRowTemp.elementAt(8));
                            if(loc!=null&&!loc.equals("")){
                                oP.setLocalidad(oP.buscarCiudad(edo,mun,loc));
                                ciudad.setCve(loc);
                                ciudad.setDescrip(oP.getLocalidad());
                                ciudad.setEdo(oP.getEdo());
                                ciudad.setMpio(oP.getMun());
                                oP.setCd(ciudad);
                            }
                        }
                    }else
                        oP.setPais("México");  
                    if(((String) vRowTemp.elementAt(9)!=null)&&!(((String) vRowTemp.elementAt(9)).equals("")))
                        oP.setCodigoPostal((String) vRowTemp.elementAt(9));
                    
            }
            return oP;
        }
